INSERT INTO `jos_content` VALUES (15,'ИЗ ЖИЗНИ НАШЕЙ ГИМНАЗИИ','-2','','<p style=\"text-align: left;\"><span style=\"font-size: medium;\" mce_style=\"font-size: medium;\">На официальном портале городской Думы и администрации города Ростова-на-Дону <b>(http://www.rostov-gorod.ru</b>) в разделе \"<span style=\"color: rgb(102, 102, 102); line-height: 1.4em; text-align: justify;\" mce_style=\"color: #666666; line-height: 1.4em; text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\" mce_style=\"color: #000000;\">Распространение передового опыта учителей информатики\" г. Ростова-на-Дону&nbsp;</span></span><span style=\"line-height: 1.3em;\" mce_style=\"line-height: 1.3em;\">были размешены разработки уроков информатики и ИКТ Стасевой М.С.</span></span></p><p style=\"text-align: left;\"><span style=\"font-size: medium;\" mce_style=\"font-size: medium;\"><span style=\"line-height: 1.3em;\" mce_style=\"line-height: 1.3em;\">Ссылки для просмотра:&nbsp;</span></span></p><p style=\"text-align: left;\"><span style=\"font-size: medium;\" mce_style=\"font-size: medium;\"><span style=\"line-height: 1.3em;\" mce_style=\"line-height: 1.3em;\"></span></span><a href=\"http://www.rostov-gorod.ru/?ID=23431\" mce_href=\"http://www.rostov-gorod.ru/?ID=23431\" style=\"line-height: 1.3em; font-size: medium;\">http://www.rostov-gorod.ru/?ID=23431</a></p><p style=\"text-align: left;\"><a href=\"http://www.rostov-gorod.ru/?ID=23580\" mce_href=\"http://www.rostov-gorod.ru/?ID=23580\" style=\"\"><span style=\"font-size: medium;\" mce_style=\"font-size: medium;\">http://www.rostov-gorod.ru/?ID=23580</span></a><br /></p><p style=\"text-align: left;\"><br /></p><p style=\"text-align: center;\" mce_style=\"text-align: center;\"><br /></p><p style=\"text-align: center;\" mce_style=\"text-align: center;\"><br /></p><p style=\"text-align: center;\" mce_style=\"text-align: center;\"><br /></p><p style=\"text-align: center;\" mce_style=\"text-align: center;\"><b style=\"\"><span style=\"font-size: med